import 'package:flutter/material.dart';class Login extends StatefulWidget {  @override  State<StatefulWidget> createState() {    return new LoginClass();  }}class LoginClass extends State<Login> {  final TextEditingController _userController = new TextEditingController();  final TextEditingController _passwordController = new TextEditingController();  @override  Widget build(BuildContext context) {    return new Scaffold(      backgroundColor: Colors.white70,      appBar: new AppBar(        backgroundColor: Colors.purple,        centerTitle: true,        elevation: 10.9,        title: new Text(          "LOGIN",          textAlign: TextAlign.center,          style: new TextStyle(              color: Colors.white, fontWeight: FontWeight.w900, fontSize: 30.0),        ),      ),      body: new Container(        alignment: Alignment.topCenter,        child: new Column(          children: <Widget>[            new Image.asset(              "images/ic_launcher.png", width: 90.0,              height: 90.0, //color: Colors.greenAccent,            ),            //form            new Container(              width: 380.0,              height: 180.0,              color: Colors.white,              child: new Column(                children: <Widget>[                  new TextField(                    controller: _userController,                    decoration: new InputDecoration(                        hintText: 'User name', icon: new Icon(Icons.person)),                  ),                  new TextField(                    controller: _passwordController,                    decoration: new InputDecoration(                        hintText: 'Password', icon: new Icon(Icons.lock)),                  ),                  new Center(                      child: new Row(                    children: <Widget>[                      new Container(                        //Login button                        child: new RaisedButton(                          onPressed: null,                          color: Colors.redAccent,                          child: new Text(                            "Login",                            style: new TextStyle(                              color: Colors.white,                              fontSize: 18.0,                            ),                          ),                        ),                      )                    ],                  ))                ],              ),            )          ],        ),      ),    );  }}